mapper.select:
SELECT drr.id,drr.dataName,drr.industryTypeId,drr.subjectAreaId,drr.dataOpenTypeId,drr.reviewTypeId,u1.userName AS submitName,drr.createTime,it.industryName,sa.subjectName,dot.dataOpenTypeName,rt.reviewName AS reviewStatus,CAST(drr.remittanceType AS SIGNED) AS remittanceType,CASE
                                                                                                                                                                                                                                                                                        WHEN drr.remittanceType='0' THEN '新增'
                                                                                                                                                                                                                                                                                        WHEN drr.remittanceType='1' THEN '更新'
                                                                                                                                                                                                                                                                                        WHEN drr.remittanceType='2' THEN '重提'
                                                                                                                                                                                                                                                                                        ELSE ''
    END AS 'remittanceTypeView',u2.userName AS reviewName FROM `data_remittance_review` drr LEFT JOIN industry_type it ON drr.industryTypeId=it.id LEFT JOIN subject_area sa ON drr.subjectAreaId=sa.id LEFT JOIN data_open_type dot ON drr.dataOpenTypeId=dot.id LEFT JOIN review_type rt ON drr.reviewTypeId=rt.id LEFT JOIN `user` u1 ON drr.createBy=u1.userId LEFT JOIN review_audit_record rar ON drr.id=rar.reviewId LEFT JOIN audit_record ar ON rar.auditRecordId=ar.id LEFT JOIN `user` u2 ON ar.reviewBy=u2.userId


        dataName LIKE concat('%',#{dataName},'%')


        and industryTypeId=${industryTypeId}


        and subjectAreaId=${subjectAreaId}


        and dataOpenTypeId=${dataOpenTypeId}


        and reviewTypeId=${reviewTypeId}


        and remittanceType=${remittanceType}



        submitName LIKE concat('%',#{submitName},'%')


        reviewName LIKE concat('%',#{reviewName},'%')


        and createTime >= #{createStartTime}


        and createTime <= #{createEndTime}

LIMIT ${starIndex}, ${pageSize};

SELECT COUNT(drr.id) FROM `data_remittance_review` drr LEFT JOIN industry_type it ON drr.industryTypeId=it.id LEFT JOIN subject_area sa ON drr.subjectAreaId=sa.id LEFT JOIN data_open_type dot ON drr.dataOpenTypeId=dot.id LEFT JOIN review_type rt ON drr.reviewTypeId=rt.id LEFT JOIN `user` u1 ON drr.createBy=u1.userId LEFT JOIN review_audit_record rar ON drr.id=rar.reviewId LEFT JOIN audit_record ar ON rar.auditRecordId=ar.id LEFT JOIN `user` u2 ON ar.reviewBy=u2.userId


        dataName LIKE concat('%',#{dataName},'%')


        and industryTypeId=${industryTypeId}


        and subjectAreaId=${subjectAreaId}


        and dataOpenTypeId=${dataOpenTypeId}


        and reviewTypeId=${reviewTypeId}


        and remittanceType=${remittanceType}



        submitName LIKE concat('%',#{submitName},'%')


        reviewName LIKE concat('%',#{reviewName},'%')


        and createTime >= #{createStartTime}


        and createTime <= #{createEndTime}
